mapper.select:
SELECT *
FROM board


        OR write_id = #{writeId}


        OR modify_id = #{modifyId}


ORDER BY board_id ASC

    LIMIT ${pageSize}


    OFFSET ${offSet}

SELECT *
FROM board


        AND board_id = #{boardId}


        AND write_id = #{writeId}


        AND modify_id = #{modifyId}



    ORDER BY board_id DESC LIMIT 1

mapper.update:
UPDATE board
board_title = #{boardTitle}, board_contents = #{boardContents}, modify_id = #{modifyId}, modify_date = #{modifyDate},
WHERE board_id = #{boardId}

mapper.delete:
DELETE
FROM board


        WHERE write_id = #{writeId}


        WHERE modify_id = #{modifyId}


        WHERE board_id = #{boardId}
